def compute_goes_flux(scaled_counts):
    '''
    Estimates the GOES flux depending on the scaled STIX counts (Xiao et al. 2023)
    
    There is a better formula by Muriel Stiefel. For future use implement that formula to get better estimates especially for stronger flares.
    
    parameters:
    scaled counts   (float)     STIX counts for 4-10keV scaled based on SOLOS heliocentric distance
    
    output:
    goes_flux       (float)     estimate of the GOES flux
    '''
    # f = 10**(-7.376+0.622 log10(X0))
    goes_flux = 10**(-7.376 + 0.622 * math.log10(scaled_counts))
    
    return goes_flux # estimate

def get_goes_classification(flux):
    '''
    simple function to convert estimated GOES flux into the better known GOES class scheme.
    
    parameters:
    flux            (float)     the flux value one would like to convert
    
    outputs:
    classification  (string)    converted GOES class
    '''
    if (flux < 10**-7):
        classification = "A"
    elif (flux < 10**-6):
        classification = "B" + str(math.floor(flux / 10**-7))
    elif (flux < 10**-5):
        classification = "C" + str(math.floor(flux / 10**-6))
    elif (flux < 10**-4):
        classification = "M" + str(math.floor(flux / 10**-5))
    else:
        classification = "X" + str(math.floor(flux / 10**-4))
    
    return classification

def histogram_variable(data, filename = f'{config.OUTPUT_DIR}/Images/Hist/histogram.jpg', xlabel = '', data_all = []):
    '''
    Function to make a histogram.
    Primarily used to generate a histogram of the statistical distribution of flare strengths.
    
    parameters:
    data:       vector with data that should be plotted in the histogram
    filename:   string of location and name the histogram should be saved at
    '''
    if data == []:
        return
    
    plt.clf()
        
    plt.xlabel(xlabel)
    plt.ylabel('Flares')
    
    if data_all == []:
        variable_counts = dict(sorted(Counter(data).items()))
    else:
        counts_all = dict(sorted(Counter(data_all).items()))
        variable_counts = dict(sorted(Counter(data).items()))
        variable_counts = {key: variable_counts[key] if key in variable_counts.keys() else 0 for key in counts_all.keys()}

        sum_var = sum(variable_counts.values())
        sum_all = sum(counts_all.values())
        
        counts_all = {a: b / sum_all * sum_var for a, b in counts_all.items()}
        df_all = pd.DataFrame.from_dict(counts_all, columns = ['expected distribution'], orient='index')
        df_all = df_all.reindex(['B2', 'B3', 'B4', 'B5', 'B6', 'B7', 'B8', 'B9', 'C1', 'C2', 'C3', 'C4', 'C5', 'C6', 'C7', 'C8', 'C9', 'M1', 'M2', 'M3', 'M4', 'M5', 'M6', 'M7', 'M8', 'M9', 'X1', 'Att.'])
        
        std = []
        for i in df_all['expected distribution']:
            std.append(math.sqrt(i))
        
        ax = df_all.plot(kind = 'bar', yerr = std, align='edge', width = .4, color = 'orange')
    
    df = pd.DataFrame.from_dict(variable_counts, columns = ['estimated GOES classification'], orient='index')
    df = df.reindex(['B2', 'B3', 'B4', 'B5', 'B6', 'B7', 'B8', 'B9', 'C1', 'C2', 'C3', 'C4', 'C5', 'C6', 'C7', 'C8', 'C9', 'M1', 'M2', 'M3', 'M4', 'M5', 'M6', 'M7', 'M8', 'M9', 'X1', 'Att.'])
    
    if data_all == []:
        df.plot(kind = 'bar', logy = True)
    else:
        df.plot(kind = 'bar', align='edge', width = -.4, ax = ax, logy = True)
            
    plt.xlim(plt.xlim()[0] - .4, plt.xlim()[1] + .4)
    plt.ylim(0, plt.ylim()[1])
    
    plt.plot(0, 0, color = 'black', label = "poisson error")
    
    handles, labels = plt.gca().get_legend_handles_labels()
    if data_all == []:
        order = [0, 1]
    else:
        order = [2, 1, 0]
    plt.legend([handles[idx] for idx in order],[labels[idx] for idx in order])
    
    plt.savefig(filename, bbox_inches = 'tight')
    
    return

def histogram_vis(data1, data2, filename = f'{config.OUTPUT_DIR}/Images/Hist/histogram.jpg', xlabel = ''):
    '''
    Function to make a histogram. Primarily used to generate a histogram of the distribution of flares depending on the visibility by Earth.
    
    parameters:
    data:       vector with data that should be plotted in the histogram
    filename:   string of location and name the histogram should be saved at
    '''
    if data1 == []:
        return
    
    plt.clf()
    
    variable_counts1 = dict(sorted(Counter(data1).items()))
    variable_counts2 = dict(sorted(Counter(data2).items()))
    df = pd.DataFrame.from_dict({'Flares not visible from Earth' : variable_counts2, 'Flares visible from Earth': variable_counts1})
    # df.plot.bar()
    df.plot.bar(logy = True, width = 0.75)
    
    plt.xlabel('estimated GOES classification')
    plt.ylabel('#-flares')
    
    plt.savefig(filename, bbox_inches = 'tight')
    
    return

goes_classification = []
vis_earth = []
not_vis_earth = []

for i in range(len(stix_flares)):
    counts = stix_flares['4-10 keV'][i] - stix_flares['bkg 4-10 keV'][i]
    
    if stix_flares['att_in'][i]:
        # print("For flare: " + str(i) + " the attenuator was in place.")
        
        classification = "Att."
    
    else:
        dist = stix_flares['solo_position_AU_distance'][i]
        scale = dist**2
        
        goes_flux = compute_goes_flux(counts * scale / 4) # STIX list has counts over 4s in it
        
        classification = get_goes_classification(goes_flux)
        
        if (stix_flares['visible_from_earth'][i]):
            vis_earth.append(classification)
        else:
            not_vis_earth.append(classification)
    
    goes_classification.append(classification)

os.makedirs(f"{config.OUTPUT_DIR}/Images/Hist/", exist_ok=True)
histogram_variable(goes_classification, f"{config.OUTPUT_DIR}/Images/Hist/all_flare_classes.jpg", "estimated GOES class")

